# # Based on:
# http://blade.nagaokaut.ac.jp/cgi-bin/scat.rb/ruby/ruby-talk/139290
# Author: Endy Tjahjono

class String
   def perm
       return [self] if self.length < 2
       ret = []

       0.upto(self.length - 1) do |n|
          #rest = self.split('')
          rest = self.split(//u)            # for UTF-8 encoded strings
          picked = rest.delete_at(n)
          rest.join.perm.each { |x| ret << picked + x }
       end

       ret
   end
end

#Test
p "abc".perm      #=>  ["abc", "acb", "bac", "bca", "cab", "cba"]
# Source: http://snippets.dzone.com/posts/show/2108

class Class
  alias_method :orig_new, :new

  @@count = 0
  @@stoppit = false
  @@class_caller_count = Hash.new{|hash,key| hash[key] = Hash.new(0)}

  def new(*arg,&blk)
    unless @@stoppit
      @@stoppit = true
      @@count += 1
      @@class_caller_count[self][caller[0]] += 1
      @@stoppit = false
    end
    orig_new(*arg,&blk)
  end

  def Class.report_final_tally
    @@stoppit = true
    puts "Number of objects created = #{@@count}"

    total = Hash.new(0)

    @@class_caller_count.each_key do |klass|
      caller_count = @@class_caller_count[klass]
      caller_count.each_value do |count|
        total[klass] += count
      end
    end

    klass_list = total.keys.sort{|klass_a, klass_b|
      a = total[klass_a]
      b = total[klass_b]
      if a != b
        -1* (a <=> b)
      else
        klass_a.to_s <=> klass_b.to_s
      end
    }
    klass_list.each do |klass|
      puts "#{total[klass]}\t#{klass} objects created."
      caller_count = @@class_caller_count[ klass]
      caller_count.keys.sort_by{|call| -1*caller_count[call]}.each do |call|
        puts "\t#{call}\tCreated #{caller_count[call]} #{klass} objects."
      end
      puts
    end
  end
end

#Test
END {
  b = String.new

  5.times do
    c = String.new
  end

  c = Array.new(12)

  Class.report_final_tally





		
		

