class Array
  def insert_at_random *obj
    insert(rand(size+1), *obj)
  end

  def randomize
    size.times { insert_at_random pop }
    self
  end
end

#Test
a = [0, 1, 2, 3, 4, 5, 6]
puts a.randomize().join(';')
